import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { getDecodedToken } from "./utils/auth";
import UserHeaderNav from "./UserHeaderNav";

const UserDashboard = () => {
  const [quizList, setQuizList] = useState([]);
  const [selectedQuiz, setSelectedQuiz] = useState(null);
  const [quizQuestions, setQuizQuestions] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [marks, setMarks] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const [username, setUsername] = useState("");
  const [userId, setUserId] = useState(null);
  const [activePage, setActivePage] = useState("dashboard"); // yeni ekleniyor
  const [solvedQuizzes, setSolvedQuizzes] = useState([]); // yeni ekleniyor

  const token = localStorage.getItem("jwt_token");

  const fetchQuizzes = useCallback(async () => {
    try {
      const response = await axios.get("http://localhost:8080/api/quizzes", {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log("Sunucudan gelen quiz verisi:", response.data);
      setQuizList(response.data);
    } catch (error) {
      console.error("Error fetching quizzes", error);
    }
  }, [token]);

  const handleQuizSelect = useCallback(async (quizId) => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/quizzes/getQuizQuestById/${quizId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setQuizQuestions(response.data);
      console.log("Quiz Questions:", response.data);
      setSelectedQuiz(quizId);
      setSelectedOptions([]);
      setShowResults(false);
      setMarks(0);
    } catch (error) {
      console.error("Error fetching quiz questions", error);
    }
  }, [token]);

  const handleOptionChange = (questionId, optionLetter) => {
    setSelectedOptions((prev) => {
      const updated = [...prev];
      const index = updated.findIndex((o) => o.questionId === questionId);

      if (index !== -1) {
        updated[index] = { questionId, optionNumber: optionLetter };
      } else {
        updated.push({ questionId, optionNumber: optionLetter });
      }

      return updated;
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      let newMarks = 0;

      for (const userAnswer of selectedOptions) {
        const { questionId, optionNumber } = userAnswer;

        const userAnswers = {
          user: {id: userId},
          question: {id: questionId} ,
          selectedOption: optionNumber,
        };
        console.log("Gönderilen userAnswers:", userAnswers);
        await axios.post("http://localhost:8080/api/user-answers/submit", userAnswers, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const selectedQuestion = quizQuestions.find(
          (q) => q.id === questionId
        );
        if (selectedQuestion.correctAnswer === optionNumber) {
          newMarks += 1;
        }
      }

      setMarks(newMarks);
      setShowResults(true);
    } catch (error) {
      console.error("Error submitting quiz answers", error);
    }
  };

  useEffect(() => {
    const decoded = getDecodedToken();
    if (decoded) {
      try {
        setUsername(decoded.username || decoded.sub);
        setUserId(decoded.userId || decoded.id);
      } catch (err) {
        console.error("Token çözümlenemedi:", err);
      }
    }

    fetchQuizzes();
  }, [token, fetchQuizzes]);

useEffect(() => {
  if (activePage === "solved-quizzes") {
    const token = localStorage.getItem("jwt_token");
    axios
      .get("http://localhost:8080/api/user/solved-quizzes", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setSolvedQuizzes(res.data))
      .catch((err) => console.error("Çözülen quizler alınırken hata:", err));
  }
}, [activePage]);

  return (
    <>
      <UserHeaderNav username={username} />
      <div className="container">
        <h2>User Dashboard</h2>
        <h3>Available Quizzes</h3>
        <ul className="list-group">
          {quizList.map((quiz) => (
            <li
              key={quiz.id}
              className="list-group-item d-flex justify-content-between align-items-center"
            >
              <span style={{ color: "black" }}>{quiz.name}</span>
              <button
                className="btn btn-primary mt-2"
                onClick={() => handleQuizSelect(quiz.id)}
              >
                Start Quiz
              </button>
            </li>
          ))}
        </ul>

        {selectedQuiz && quizQuestions.length > 0 && !showResults && (
          <div>
            <h3>Quiz Questions</h3>
            {quizQuestions.map((question) => (
              <div key={question.id} className="mb-3">
                <h4>{question.content}</h4>
                <ul className="list-group">
                  {["A", "B", "C", "D"].map((letter,index) => (
                    <li key={letter} className="list-group-item">
                      <input
                        type="radio"
                        name={`question-${question.id}`}
                        value={index + 1}
                        onChange={() =>
                          handleOptionChange(question.id, index+1)}
                      />
                      {question[`option${letter}`]}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
            <button className="btn btn-primary mt-3" onClick={handleSubmit}>
              Submit Quiz
            </button>
          </div>
        )}

        {showResults && (
          <div className="mt-4">
            <h3>Results</h3>
            <p>
              You scored {marks} out of {quizQuestions.length}.
            </p>
            {quizQuestions.map((question) => {
              const userAnswer = selectedOptions.find(
                (opt) => opt.questionId === question.id
              );
              const isCorrect =
                userAnswer?.optionNumber === question.correctAnswer;

              return (
                <div key={question.id} className="mb-3">
                  <h4>{question.content}</h4>
                  <p>
                    <strong>Your answer:</strong>{" "}
                    {question[`option${userAnswer?.optionNumber}`]}
                  </p>
                  <p className={isCorrect ? "text-success" : "text-danger"}>
                    {isCorrect
                      ? "Correct!"
                      : `Correct answer: ${
                          question[`option${question.correctAnswer}`]
                        }`}
                  </p>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </>
  );
};

export default UserDashboard;
