import React, { useEffect } from 'react';
import axios from "axios";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Login from "./Login";
import AdminDashboard from "./AdminDashboard"; // Admin için dashboard
import UserDashboard from "./UserDashboard"; // Kullanıcı için dashboard
import AdminPrivateRoute from "./AdminPrivateRoute"; // Admin'e özel sayfalara yönlendirme
import UserPrivateRoute from "./UserPrivateRoute"; // Kullanıcıya özel sayfalara yönlendirme
import Register from './Register';

const App = () => {
  // 🔐 JWT token'ı global axios header olarak ekle
  useEffect(() => {
    const token = localStorage.getItem("jwt_token");
    if (token && token !== "null" && token !== "undefined") {
      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    } else {
      delete axios.defaults.headers.common["Authorization"]; // 👈 Eğer geçerli token yoksa header’ı sil
    }
  }, []);

  return (
    <Router>
      <Routes>
        {/* Giriş sayfası */}
        <Route path="/" element={<Login />} />
        {/* Kayıt sayfası */}
        <Route path="/register" element={<Register />} />

        {/* Admin ve User Dashboard için özel route'lar */}
        <Route
          path="/admin-dashboard"
          element={
            <AdminPrivateRoute>
              <AdminDashboard />
            </AdminPrivateRoute>
          }
        />
        <Route
          path="/user-dashboard"
          element={
            <UserPrivateRoute>
              <UserDashboard />
            </UserPrivateRoute>
          }
        />
      </Routes>
    </Router>
  );
};

export default App;
