import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // ✅ navigate importu

function Register() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const navigate = useNavigate(); // ✅ hook tanımı

  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      // 1. Kayıt isteği gönder
      const response = await axios.post('/api/register', {
        username,
        email,
        password,
      });

      console.log('Kayıt başarılı:', response.data);

      // 2. Kayıt başarılıysa → otomatik giriş yap
      const loginResponse = await axios.post('/api/login', {
        username,
        password,
      });

      const token = loginResponse.data.token || loginResponse.data; // JWT ise bu işe yarar
      localStorage.setItem('token', token);

      setMessage('✅ Kayıt ve giriş başarılı! Yönlendiriliyor...');

      // 3. Yönlendir → user dashboard
      return navigate('/user-dashboard');

    } catch (error) {
      const serverMessage = error.response?.data;

      if (error.response?.status === 409) {
        if (serverMessage === "Kullanıcı adı zaten var" || serverMessage === "E-posta zaten kayıtlı") {
          setMessage("❗ " + serverMessage);
        } else {
          setMessage("⚠️ Kayıt reddedildi.");
        }
      } else {
        console.error("Hata:", error);
        setMessage("Sunucu hatası! Lütfen tekrar deneyin.");
      }
    }
  };

  return (
    <div className="container mt-5">
      <h2>Kayıt Ol</h2>
      <form onSubmit={handleRegister}>
        <div className="mb-3">
          <label>Kullanıcı Adı:</label>
          <input
            type="text"
            className="form-control"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label>Email:</label>
          <input
            type="email"
            className="form-control"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label>Şifre:</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Kayıt Ol</button>
      </form>

      {message && <div className="alert mt-3">{message}</div>}
    </div>
  );
}

export default Register;
