import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { getDecodedToken } from "./utils/auth";
const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:8080/api/login", {
        username: username,
        password: password,
      });

      const token = response.data;  // Backend'den gelen JWT token

            // JWT token'ını localStorage'da saklıyoruz
            localStorage.setItem("jwt_token", token);

            // JWT token'ı decode ederek kullanıcı rolünü alıyoruz
            const decodedToken = getDecodedToken();
            const role = decodedToken?.role;

            // Role göre yönlendirme yapıyoruz
            if (role === "admin") {
              navigate("/admin-dashboard", { state: { username: username } });  // Admin paneline yönlendir
            } else if (role === "user") {
              navigate("/user-dashboard", { state: { username: username } });  // Kullanıcı paneline yönlendir
            } else {
              setError("Invalid credentials");
            }
          } catch (error) {
            console.error("Login hatası:", error); // bu satır çıktıyı gösterir
              if (error.response) {
                console.error("Sunucu Yanıtı:", error.response.data);
                setError(error.response.data || "Sunucu hatası. Lütfen tekrar deneyin.");
              } else if (error.request) {
                setError("Sunucuya ulaşılamıyor. Lütfen bağlantınızı kontrol edin.");
              } else {
                setError("Bilinmeyen bir hata oluştu.");
              }
          }
        };
const goToRegister = () => {
    navigate("/register");
  };
  return (
    <div className="container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            className="form-control"
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            className="form-control"
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button className="btn btn-primary" type="submit">
          Log In
        </button>
        {error && <div className="text-danger">{error}</div>}
      </form>
    <div className="mt-3">
            <span>Hesabın yok mu? </span>
            <button className="btn btn-link p-0 align-baseline" onClick={goToRegister}>
              Kayıt Ol
            </button>
          </div>
    </div>
  );
};

export default Login;
