import React, { useState, useEffect } from "react";
import axios from "axios";
import jwt_decode from "jwt-decode";
import UserHeaderNav from "./UserHeaderNav";

const UserDashboard = () => {
  const [quizList, setQuizList] = useState([]);
  const [selectedQuiz, setSelectedQuiz] = useState(null);
  const [quizQuestions, setQuizQuestions] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [marks, setMarks] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const [username, setUsername] = useState("");
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("jwt_token");
    if (token) {
      try {
        const decoded = jwt_decode(token);
        setUsername(decoded.username || decoded.sub);
        setUserId(decoded.userId || decoded.id);
      } catch (err) {
        console.error("Token çözümlenemedi:", err);
      }
    }

const fetchQuizzes = async () => {
  try {
    const token = localStorage.getItem("jwt_token");  // ✅ Token al
    const response = await axios.get("http://localhost:8080/api/quizzes", {
      headers: {
        Authorization: `Bearer ${token}`, // ✅ Header'a ekle
      },
    });
    setQuizList(response.data);
  } catch (error) {
    console.error("Error fetching quizzes", error);
  }
};


    fetchQuizzes();
  }, []);

  const handleQuizSelect = async (quizId) => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/quizzes/getQuizQuestById/${quizId}`
      );
      setQuizQuestions(response.data);
      setSelectedQuiz(quizId);
      setSelectedOptions([]);
      setShowResults(false);
      setMarks(0);
    } catch (error) {
      console.error("Error fetching quiz questions", error);
    }
  };

  const handleOptionChange = (questionId, optionNumber) => {
    setSelectedOptions((prevSelectedOptions) => {
      const updatedOptions = [...prevSelectedOptions];
      const questionIndex = updatedOptions.findIndex(
        (option) => option.questionId === questionId
      );

      if (questionIndex !== -1) {
        updatedOptions[questionIndex] = { questionId, optionNumber };
      } else {
        updatedOptions.push({ questionId, optionNumber });
      }

      return updatedOptions;
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      let newMarks = 0;

      for (const userAnswer of selectedOptions) {
        const { questionId, optionNumber } = userAnswer;

        const userAnswers = {
          userId: userId,
          questionId: questionId,
          selectedOption: optionNumber,
        };

        await axios.post("http://localhost:8080/api/user-answers", userAnswers);

        const selectedQuestion = quizQuestions.find(
          (question) => question.id === questionId
        );
        if (selectedQuestion.correctOption === optionNumber) {
          newMarks += 1;
        }
      }

      setMarks(newMarks);
      setShowResults(true);
    } catch (error) {
      console.error("Error submitting quiz answers", error);
    }
  };

  return (
    <>
      <UserHeaderNav username={username} />
      <div className="container">
        <h2>User Dashboard</h2>
        <h3>Available Quizzes</h3>
        <ul className="list-group">
          {quizList.map((quiz) => (
            <li key={quiz.id} className="list-group-item">
              <span>{quiz.quizName}</span>
              <button
                className="btn btn-primary ml-2"
                onClick={() => handleQuizSelect(quiz.id)}
              >
                Start Quiz
              </button>
            </li>
          ))}
        </ul>

        {selectedQuiz && quizQuestions.length > 0 && !showResults && (
          <div>
            <h3>Quiz Questions</h3>
            {quizQuestions.map((question) => (
              <div key={question.id} className="mb-3">
                <h4>{question.questionText}</h4>
                <ul className="list-group">
                  {["option1", "option2", "option3", "option4"].map((option, index) => (
                    <li key={index} className="list-group-item">
                      <input
                        type="radio"
                        name={`question-${question.id}`}
                        value={index + 1}
                        onChange={() => handleOptionChange(question.id, index + 1)}
                      />
                      {question[option]}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
            <button className="btn btn-primary mt-3" onClick={handleSubmit}>
              Submit Quiz
            </button>
          </div>
        )}

        {showResults && (
          <div className="mt-4">
            <h3>Results</h3>
            <p>You scored {marks} out of {quizQuestions.length}.</p>
            {quizQuestions.map((question) => {
              const userAnswer = selectedOptions.find(
                (opt) => opt.questionId === question.id
              );
              const isCorrect =
                userAnswer?.optionNumber === question.correctOption;

              return (
                <div key={question.id} className="mb-3">
                  <h4>{question.questionText}</h4>
                  <p>
                    <strong>Your answer:</strong>{" "}
                    {question[`option${userAnswer?.optionNumber}`]}
                  </p>
                  <p className={isCorrect ? "text-success" : "text-danger"}>
                    {isCorrect
                      ? "Correct!"
                      : `Correct answer: ${question[`option${question.correctOption}`]}`}
                  </p>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </>
  );
};

export default UserDashboard;
