import jwt_decode from "jwt-decode";

export const getDecodedToken = () => {
  try {
    const token = localStorage.getItem("jwt_token");
    if (!token) return null;
    return jwt_decode(token);
  } catch (error) {
    console.error("Token decode edilemedi:", error.message);
    return null;
  }
};
