import React, { useState } from 'react';
import axios from 'axios';

function Register() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/register', {
        username,
        email,
        password,
      });
      setMessage('Kayıt başarılı!');
      console.log('Kayıt başarılı:', response.data);
    const loginResponse = await axios.post('/api/login', {
            username,
            password,
          });
      // 3. Giriş başarılıysa → token/session sakla
      const token = loginResponse.data.token; // Eğer JWT veriyorsa
      localStorage.setItem('token', token); // veya sessionStorage

      setMessage('Kayıt ve giriş başarılı! Yönlendiriliyor...');

      // 4. Yönlendir
      navigate('/user-dashboard');
    }

    catch (error) {
     const serverMessage = error.response?.data;

       if (error.response?.status === 409) {
         if (serverMessage === "Kullanıcı adı veya email adresi zaten var") {
           setMessage("❗ Kullanıcı adı veya email adresi zaten kayıtlı.");
         }
         else {
           setMessage("⚠️ Kayıt reddedildi.");
         }
       }
       else {
         setMessage("Sunucu hatası! Lütfen tekrar deneyin.");
       }
    }
  };

  return (
    <div className="container mt-5">
      <h2>Kayıt Ol</h2>
      <form onSubmit={handleRegister}>
        <div className="mb-3">
          <label>Kullanıcı Adı:</label>
          <input
            type="text"
            className="form-control"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label>Email:</label>
          <input
            type="email"
            className="form-control"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label>Şifre:</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Kayıt Ol</button>
      </form>

      {message && <div className="alert mt-3">{message}</div>}
    </div>
  );
}

export default Register;
