import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { getDecodedToken } from "./utils/auth";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:8080/api/login", {
        username: username,
        password: password
      }, {
        headers: {} // ðŸ‘ˆ override edip boÅŸ gÃ¶nder
      });


      if (!response.data?.token) {
        setError("GiriÅŸ baÅŸarÄ±sÄ±z. YanÄ±t hatalÄ±.");
        return;
      }
      console.log("Gelen cevap:", response.data)
      const token = response.data.token;
      localStorage.setItem("jwt_token", token);
      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
      const decodedToken = getDecodedToken();
      const role = decodedToken?.role;

      if (role === "ADMIN") {
        navigate("/admin-dashboard");
      } else if (role === "USER") {
        navigate("/user-dashboard");
      } else {
        setError("GeÃ§ersiz kullanÄ±cÄ± rolÃ¼");
      }

    } catch (error) {
      console.error("Login hatasÄ±:", error);
      if (error.response) {
        setError(error.response.data || "Sunucu hatasÄ±");
      } else {
        setError("BaÄŸlantÄ± hatasÄ±");
      }
    }
  };

  const goToRegister = () => navigate("/register");

  return (
    <div className="container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Username:</label>
          <input className="form-control" type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input className="form-control" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <button className="btn btn-primary" type="submit">Log In</button>
        {error && <div className="text-danger mt-2">{error}</div>}
      </form>
      <div className="mt-3">
        <span>HesabÄ±n yok mu? </span>
        <button className="btn btn-link p-0" onClick={goToRegister}>KayÄ±t Ol</button>
      </div>
    </div>
  );
};

export default Login;
