import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { getDecodedToken } from "./utils/auth";
const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:8080/api/login", {
        username: username,
        password: password,
      });
    if (typeof response.data !== "object" || !response.data.token) {
        setError("GiriÅŸ baÅŸarÄ±sÄ±z. YanÄ±t hatalÄ±.");
        return;
    }
      console.log("Gelen cevap:", response.data);

      const token = response.data;  // Backend'den gelen JWT token

            // JWT token'Ä±nÄ± localStorage'da saklÄ±yoruz
            localStorage.setItem("jwt_token", token);

            // JWT token'Ä± decode ederek kullanÄ±cÄ± rolÃ¼nÃ¼ alÄ±yoruz
            const decodedToken = getDecodedToken();
            if (!decodedToken) {
              setError("Token doÄŸrulanamadÄ±. LÃ¼tfen tekrar giriÅŸ yapÄ±n.");
              return;
            }
            const role = decodedToken?.role;

            // Role gÃ¶re yÃ¶nlendirme yapÄ±yoruz
            if (role === "admin") {
              navigate("/admin-dashboard", { state: { username: username } });  // Admin paneline yÃ¶nlendir
            } else if (role === "user") {
              navigate("/user-dashboard", { state: { username: username } });  // KullanÄ±cÄ± paneline yÃ¶nlendir
            } else {
              setError("Invalid credentials");
            }
          } catch (error) {
              console.error("Login hatasÄ±:", error);

              if (error.response) {
                console.error("ğŸ”´ Sunucu YanÄ±tÄ±:", error.response);
                setError(error.response.data || "Sunucu hatasÄ±. LÃ¼tfen tekrar deneyin.");
              } else if (error.request) {
                console.error("ğŸŸ  Sunucuya istek gÃ¶nderildi ama yanÄ±t alÄ±namadÄ±.");
                setError("Sunucuya ulaÅŸÄ±lamÄ±yor. LÃ¼tfen baÄŸlantÄ±nÄ±zÄ± kontrol edin.");
              } else {
                console.error("ğŸŸ¡ Hata mesajÄ±:", error.message);
                setError("Bilinmeyen bir hata oluÅŸtu: " + error.message);
              }
            }

        };
const goToRegister = () => {
    navigate("/register");
  };
  return (
    <div className="container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            className="form-control"
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            className="form-control"
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button className="btn btn-primary" type="submit">
          Log In
        </button>
        {error && <div className="text-danger">{error}</div>}
      </form>
    <div className="mt-3">
            <span>HesabÄ±n yok mu? </span>
            <button className="btn btn-link p-0 align-baseline" onClick={goToRegister}>
              KayÄ±t Ol
            </button>
          </div>
    </div>
  );
};

export default Login;
